mapper.select:
select *
	from
	(
	    select rownum as RNUM, A.*
	    from
	    (
	       	select B.* 
	       	from Schedule B
	       	where s_price !=' '
	       	and a_depnm = #{depLoc}
	       	and a_arrnm = #{arrLoc}
	       	and s_starttime like '%' || #{hdDate} || '%'
	    )A
	)
	where RNUM>#{firstRecordIndex} 

	and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

	select count(*) from Schedule 
	where s_price!=' '
	and a_depnm = #{depLoc}
    and a_arrnm = #{arrLoc}
    and s_starttime like '%' || #{hdDate} || '%'
